mapper.insert:
INSERT INTO NOTICE (
	NOTICE_IDX, TITLE, CONTENT, REGDATE, MODDATE, VIEWCNT, USE_YN
)
VALUES ( 
	#{notice_idx}, #{title}, #{content}, now(), now(), 0, true
)

mapper.select:
SELECT t1.NOTICE_IDX as notice_idx, t1.TITLE as title, t1.CONTENT as content, t1.REGDATE as regDate, t1.MODDATE as modDate, t1.VIEWCNT as viewCNT, t1.USE_YN as use_YN,
t2.FILE_CD as file_cd, t2.FILE_IDX as file_idx, t2.NOTICE_IDX as fNotice_idx, t2.FILENAME as fileName, t2.FILENAME_ORG as fileName_org, t2.FILESIZE as fileSize, t2.REGDATE as fRegDate, ROUND(t2.FILESIZE/1024,1) as fileSizeByte
FROM NOTICE t1 LEFT JOIN NOTICEFILE t2 ON t1.NOTICE_IDX = t2.NOTICE_IDX WHERE t1.NOTICE_IDX = #{notice_idx}

mapper.update:
UPDATE NOTICE SET
	VIEWCNT = viewcnt+1
WHERE NOTICE_IDX = #{notice_idx}

mapper.insert:
INSERT INTO NOTICEFILE (
	FILE_CD, FILE_IDX, NOTICE_IDX, FILENAME, FILENAME_ORG, FILESIZE, REGDATE
)
VALUES ( 
	#{file_cd}, #{file_idx}, #{notice_idx}, #{filename}, #{filename_org}, #{fileSize}, now()
)
