mapper.update:
UPDATE MEMBER SET
JOIN_YN = TRUE
WHERE MEMBER_ID = #{member_id}
AND BIRTH = #{birth}

mapper.select:
SELECT PRODUCT_NM 
FROM PRODUCT
WHERE PRODUCT_CD = #{product_cd}

SELECT FILENAME_THUMB 
FROM PRODUCTIMG
WHERE PRODUCT_CD = #{product_cd}

mapper.update:
UPDATE AUCTIONINFO 
   SET MONEY_LAST = #{money_last}
     , BAY_MEMBER_ID = #{bay_member_id}
     , PAYMENT_PROC_CD = '2'
WHERE PRODUCT_CD = #{product_cd}
