mapper.select:
SELECT prodlist.* FROM ( SELECT @rownum:=@rownum+1 as rn , prod.PRODUCT_CD, prod.PRODUCT_NM, prod.MEMBER_ID, prod.STATE_CD, prod.STATE_NM, prod.VIEWCNT , IFNULL(prod.CONTENT, '') as CONTENT , (SELECT COUNT(bid.PRODUCT_CD) FROM BIDINFO bid WHERE bid.PRODUCT_CD = prod.PRODUCT_CD) as BID_CNT , IFNULL((SELECT MAX(bid.MONEY_BID) FROM BIDINFO bid WHERE bid.PRODUCT_CD = prod.PRODUCT_CD), 0) as BID_MAX_MONEY , IFNULL((SELECT prodimg.FILENAME_THUMB FROM PRODUCTIMG prodimg WHERE prodimg.PRODUCT_CD = prod.PRODUCT_CD ORDER BY prodimg.IMG_IDX ASC limit 1), '') as FILENAME_THUMB , IFNULL((SELECT DATE_FORMAT(auct.ACUTDATE_END, '%Y-%m-%d %H:%s') FROM AUCTIONINFO auct WHERE auct.PRODUCT_CD = prod.PRODUCT_CD), '') AS ACUTDATE_END_STR , IFNULL((SELECT auct.MONEY_FIRST FROM AUCTIONINFO auct WHERE auct.PRODUCT_CD = prod.PRODUCT_CD), 0) as MONEY_FIRST FROM PRODUCT prod , (SELECT @rownum:=0) tmp WHERE prod.USE_YN = TRUE AND prod.STATE_CD IN ('3', '5', '6', '7')
AND prod.CLASS_BIG_CD = #{class_big_cd}
AND prod.CLASS_MID_CD = #{class_mid_cd}
AND prod.PRODUCT_NM like CONCAT('%',#{searchVal},'%')
) prodlist WHERE prodlist.rn BETWEEN #{start_num} AND #{end_num}

SELECT *
  FROM PRODUCTIMG
 WHERE PRODUCT_CD = #{product_cd}

  SELECT bid.* 
	FROM ( SELECT  @rownum:=@rownum+1 as rowcnt, bi.*
			FROM ( SELECT *
					 FROM BIDINFO
			   	    WHERE PRODUCT_CD = #{product_cd}
				 ORDER BY REGDATE DESC
				 ) bi, (SELECT @rownum:=0) tmp 
		  ) bid
	WHERE bid.rowcnt BETWEEN #{start_num} AND #{end_num}

	SELECT COUNT(PRODUCT_CD)
	  FROM BIDINFO
     WHERE PRODUCT_CD = #{product_cd}

SELECT IFNULL(MAX(CAST(SUBSTRING(PRODUCT_CD, 11, 4) as UNSIGNED)), 0) +1 as maxCnt
  FROM PRODUCT
 WHERE SUBSTRING(PRODUCT_CD, 1, 10) = #{searchVal}

SELECT IFNULL(VIEWCNT, 0) as VIEWCNT
  FROM PRODUCT
 WHERE PRODUCT_CD = #{product_cd}

SELECT IFNULL(COUNT(PRODUCT_CD), 0) as prodCnt
  FROM PRODUCTWISH
 WHERE PRODUCT_CD = #{product_cd}
   AND MEMBER_ID = #{member_id}

mapper.update:
UPDATE PRODUCT
   SET VIEWCNT = #{viewcnt}
 WHERE PRODUCT_CD = #{product_cd}
