INSERT INTO NOTICE(TITLE, CONTENT, REGDATE) VALUES ('랜섬웨어 예방 요령', '<p style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="font-size: 12pt;"><b style="font-weight: bold;">1. 랜섬웨어 방지 대국민 행동</b></span></p><p><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">1) PC를 켜기 전 네트워크 단절</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">- 랜선 뽑기</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">- 와이파이 끄기</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">2) 감염 경로 차단</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">- 방화벽 설정 변경</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">3) 인터넷 재연결 후 보안 업데이트</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">- 윈도우 보안 패치 실행</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">- 백신 프로그램 업데이트<br></span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"></p><p style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="font-size: 12pt;"><b style="font-weight: bold;">2. 파일 공유 기능 해제 - 방화벽 설정</b></span></p><p><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">Window 방화벽에서 SMB에 사용되는 포트 차단</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">1) 제어판 -&gt; 시스템 및 보안</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">2) Windows 방화벽 -&gt; 고급 설정</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">3) 인바운드 규칙 -&gt; 새규칙 -&gt; 포트 -&gt; 다음</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">4) TCP -&gt; 특정 로컬 포트 -&gt; 139,445 -&gt; 다음</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">5) 연결 차단 -&gt; 다음</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">6) 도메인, 개인, 공용 체크 확인 -&gt; 다음</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">7) 이름 설정 -&gt; 마침</span><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;">더 자세히 알고 싶으신 분들은 아래 링크를 클릭하세요!</span></p><p><a href="http://m.post.naver.com/viewer/postView.nhn?volumeNo=5610420&amp;memberNo=3326308" target="_blank" style="background-color: rgb(255, 255, 255); color: rgb(51, 122, 183); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">클릭!!</a><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 12pt;"><br></span><br></p>', now());
INSERT INTO NOTICE(TITLE, CONTENT, REGDATE, MODDATE) VALUES ('10월 공휴일 고객센터 운영 안내', '<p>&lt;10월 공휴일 고객센터 운영 안내&gt;</p><p><br></p><p>안녕하세요. 에코베이입니다.</p><p>10월 9일은한글날인 관계로 고객센터 및 방문 상담이 어려우니</p><p>회원님들의 너그러운 양해 부탁 드립니다.&nbsp;</p><p><br></p><p>모든 상담 업무는 10월 10일(수)부터 정상 운영될 예정입니다.</p><p><br></p><p>회원님들의 협조에 감사의 말씀 드리며,</p><p>앞으로도 항상 최선을 다하는 에코베이가 될 수 있도록 노력 하겠습니다.</p><p>감사합니다.&nbsp;</p>', '2018-10-01 10:47:43', '2018-10-01 10:47:43');
INSERT INTO NOTICE(TITLE, CONTENT, REGDATE, MODDATE) VALUES ('2018년 추석 연휴 업무안내 공지', '<div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><img style="width: 790.8px;" src="/displayFile.do?fileName=/2018/10/16/98626fc7-784a-4eae-96a5-bad0f251005f_20180917144930.jpg"><span style="font-size: 18pt;"><br></span></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="font-size: 18pt;">&lt;추석 연휴 고객센터 운영 안내&gt;</span></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">9월 22일(토) 부터 9월 26일(수)까지는 추석 연휴 및 공휴일로 고객센터 및 방문 상담이 어려우니</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">회원님들의 너그러운 양해 부탁 드립니다.&nbsp;</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">모든 상담 업무는 9월 27일부터 정상 운영될 예정입니다.</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">&nbsp;</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">&nbsp;</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><span style="font-size: 18pt;">&lt;추석 연휴 체크사항&gt;</span></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">연휴 기간 내 원활한 거래 진행을 위해 아래 체크 사항을 확인해 주시기 바랍니다.</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">&nbsp;</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">■결제 및 은행 업무 날짜 체크■</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">입금신청서상의 입금 정보(입금자명, 금액, 입금액 등)가 동일할 경우</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">연휴 기간에도 30분~1시간 내에 자동으로 입금 확인이 이루어집니다.</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">(입금신청서가 없거나, 입금신청서상의 정보가 다를 경우 제외)</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">&nbsp;</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">다만, 연휴 기간에는 은행 업무가 이루어지지 않아 계좌 이체 등의 금융 거래가 어려울 수 있으니 이용하시는 은행의 업무 시간을 확인해 주시기 바라며&nbsp;</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">연휴 기간에는 이머니를 통한 바로 결제를 권장해 드리고 있으니 참고해 주시기 바랍니다.</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">■택배사 접수마감일 확인 ■</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">추석 연휴로 인해 대부분의 택배사에서는 인터넷 예약 및 방문 접수를 빠르면 9월 5일부터 늦어도 9월 21일까지 받지 않고 있습니다.</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">각 택배사별 접수 마감일은 첨부한 사진 파일을 통해 확인 부탁 드리며,</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">해당 기간 동안 이용하시는 구매 회원님께서는 배송 기간을 참고해 주시기 바랍니다.</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">☞ 정확한 배송 일정 확인은 각 택배사에 개별 문의를 부탁 드립니다.&nbsp;</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">&nbsp;</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">■배송지연■</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">추석 연휴 택배사 접수 마감으로 인해 배송이 지연될 수 있기 때문에</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">혼란을 방지하고자 9월 18일~ 9월 30일 배송 마감인 물품은 자동으로 배송 지연처리 됩니다(10월 1일까지).&nbsp;</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">추석 연휴 택배사 마감으로 인한 배송 지연건들이기에 신용도 반영 없이 기한이 연장 되는 점 참고 부탁드리며</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">구매자분들의 많은 양해 바랍니다.</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">판매취소 접수 및 추가 배송지연 요청은 [1:1 상담문의]를 통해 문의해 주시기 바랍니다.</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">&nbsp;</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">&nbsp;</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">추석 연휴 풍성하게 보내시기 바라며,</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">앞으로도 항상 최선을 다하는 에코베이가 될 수 있도록 노력 하겠습니다.</div><div style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;"><br></div><p><span style="color: rgb(51, 51, 51); font-family: &quot;Nanum Gothic&quot;, sans-serif; font-size: 14px;">감사합니다.</span><br></p>', '2018-09-17 15:42:26', '2018-09-17 15:42:26');
